Page._content:
/*gb*/
* { -webkit-tap-highlight-color: transparent; outline: 0; margin:0; padding:0; vertical-align:baseline;}
body, h1, h2, h3, h4, h5, h6, hr, p, blockquote, dl, dt, dd, ul, ol, li, pre, form, fieldset, legend, button, input, textarea, th, td { margin: 0; padding: 0; vertical-align: baseline; font-weight:normal;}
img { border: 0 none; vertical-align: top;}
i, em { font-style: normal;}
ol, ul { list-style: none;}
input, select, button, h1, h2, h3, h4, h5, h6 { font-size: 100%; font-family: inherit;}
table { border-collapse: collapse; border-spacing: 0;}
a { text-decoration: none; color: #666;}
body{max-width:700px;min-width:320px;margin:0 auto;font-size:14px;
  font-family: Helvetica, STHeiti STXihei, Microsoft JhengHei, Microsoft YaHei, Arial; line-height: 1.5; color: #666;background:#f6f6f6;}
/*setpage*/
.detail{
	width: 16rem;
	padding-bottom: 56px;
	background: #f6f6f6;
}
.detail_main{
	position: relative;
	overflow: hidden;
	background: #fff;
    border-bottom: 1px solid #e4e4e4;
    box-shadow: 0 0 5px #e4e4e4;
}
.detail_info{
	position: relative;
	height: 8.53333rem;
	overflow: hidden;
}
.detail_img{
	position: absolute;
	top: 50%;
	-webkit-transform:translateY(-50%);
	transform:translateY(-50%);
	width: 100%;
}
.detail_info:after{
	content: "";
	position: absolute;
	width: 100%;
	height: 4.26667rem;
	left: 0;
	bottom: 0;
	background: -webkit-gradient(linear,0% top,0% bottom, from(transparent), to(rgba(0,0,0,0.7)));
    background: -webkit-linear-gradient(top,rgba(0,0,0,0),rgba(0,0,0,0.7));
}
.detail_tag{
	font-size: 12px;
	text-align: center;
}
.detail_tag a{
	display: inline-block;
	padding: 8px 2px 8px 0;
	height: 18px;
}
.detail_tag_txt{
	display: block;
	padding: 0 6px;
	height: 18px;
	line-height: 18px;
	border-radius: 18px;
	color: #fff;
	background: rgba(0,0,0,0.2);
}
.detail_star{
	position: absolute;
	bottom: 6px;
	left: 0;
	font-size: 12px;
	height: 20px;
	color: rgba(255,255,255,1);
	width: 100%;
  	display: -webkit-box;
  	display: -webkit-flex;
  	display: flex;
  	z-index: 1;
}
.detail_star span{
	padding: 0 10px;
  	-webkit-box-flex: 1;
  	-webkit-flex: 1;          
  	flex: 1;  
}
.detail_love{
	position: absolute;
	right: 0;
	top: 0;
	padding: 10px;
	width: 18px;
	height: 16px;
	background: url("data:image/png;base64,") no-repeat;
	background-size: 18px 16px;
	background-position: 50% 50%;
}
.detail_love.loved{
	background: url("data:image/png;base64,") no-repeat;
	background-size: 18px 16px;
	background-position: 50% 50%;
}
.detail_cnt{
	padding: 10px 15px;
}
.detail_tit{
	position: relative;
	color: #171717;
	font-size: 18px;
	padding: 6px 0;
	overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
	text-align: center;
}
.detail_tit:after{
	content: "";
	position: absolute;
	width: 20px;
	height: 2px;
	background: #e4a558;
	left: 50%;
	bottom: 0;
	margin-left: -10px;
}
.detail_analysis{
	position: relative;
	color: #7d7d7d;
	font-size: 14px;
	line-height: 20px;
	padding-top: 10px;
}
.detail_analysis p{
	margin-bottom: 12px;
}
.detail_analysis a{
	color: #6190e8;
}
.detail_analysis .ar_full_sub{
	display: block;
	color: #ccc;
	/*padding-bottom: 12px;*/
	text-align: center;
}
.detail_analysis .figurecapture{
	font-size: 12px;
	color: #ccc;
	text-align: center;
}
.detail_analysis .img img{
	width: 100%;
	height: 100%;
}
.detail_analysis .quote{
	position: relative;
	color: #bcbcbc;
	padding: 0 16px;
	font-style: italic;
/*	border-left: 3px #ccc solid;
	padding-left: 10px;*/
}
.detail_analysis .quote:before{
	content: "“";
	position: absolute;
	font-size: 30px;
	left: 0;
	top: 0;
}
.detail_analysis .author{
	color: #ccc;
	text-align: right;
}
.detail_analysis h3{
	padding-top: 20px;
	font-size: 16px;
	color: #171717;
}
.detail_bottom_btn{
	position: fixed;
	width: 36px;
	height: 36px;
	bottom: 10px;
	border-radius: 50%;
	background: rgba(97, 144, 232, 0.8);
	z-index: 9;
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
}
.detail_bottom_btn{
	left: 10px;
}
.detail_bottom_btn:after{
	content: "";
	position: absolute;
	width: 8px;
	height: 8px;
	left: 14px;
	top: 50%;
	margin-top: -6px;
	border: 2px solid #fff;
	border-color: #fff transparent transparent #fff;
	-webkit-transform: rotate(-45deg);
	transform: rotate(-45deg);
}
.detail_bottom_right{
	position: fixed;
	width: 100px;
	height: 36px;
	bottom: 10px;
	right: 10px;
	line-height: 36px;
	text-align: center;
	border-radius: 36px;
	background: rgba(97, 144, 232, 0.8);
	z-index: 9;
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
}
.detail_bottom_right:after{
	content: "";
	width: 1px;
	height: 12px;
	position: absolute;
	background: #fff;
	top: 12px;
	left: 44px;
	opacity: 0.8;
}
.detail_bottom_love{
	position: absolute;
	height: 36px;
	width: 30px;
	padding: 0 10px;
	left: 0;
	top: 0;
	background: url("data:image/png;base64,") no-repeat;
	background-size: 18px 16px;
	background-position: 50% 50%;
}
.detail_bottom_love.loved{
	background: url("data:image/png;base64,") no-repeat;
	background-size: 18px 16px;
	background-position: 50% 50%;	
}
.detail_bottom_curvol{
	position: absolute;
	color: #fff;
	height: 36px;
	width: 35px;
	padding-right: 15px;
	right: 0;
	top: 0;
}
.detail_bottom.hide{
	-webkit-transform: translateY(50px);
	transform: translateY(50px);
}
.loading{
	position: fixed;
	top: 0;
	left: 0;
	height: 100%;
	width: 100%;
	background: #6190e8;
	z-index: 10;
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
}
.load_main{
	position: absolute;
	top: 50%;
	left: 50%;
	-webkit-transform: translate(-50%,-50%);
	transform: translate(-50%,-50%);
}
.load .main{
	stroke-dasharray: 855;
	stroke-dashoffset: 855;
	fill: rgba(0,0,0,0);
	-webkit-animation: main 2s linear infinite;
}
@-webkit-keyframes main{
	0%{ stroke-dashoffset: 855;fill: rgba(0,0,0,0);}
	25%{ stroke-dashoffset: 0;fill: rgba(0,0,0,0);}
	50%,100%{ stroke-dashoffset: 0;fill: #fff;}
}

