body{max-width:700px;min-width:320px;margin:0 auto;font-size:14px;
  font-family: Helvetica, STHeiti STXihei, Microsoft JhengHei, Microsoft YaHei, Arial; line-height: 1.5; color: #666;background:#f6f6f6;}
/*setpage*/
.detail{
	width: 16rem;
	padding-bottom: 56px;
	background: #f6f6f6;
}
.detail_main{
	position: relative;
	overflow: hidden;
	background: #fff;
    border-bottom: 1px solid #e4e4e4;
    box-shadow: 0 0 5px #e4e4e4;
}
.detail_info{
	position: relative;
	height: 8.53333rem;
	overflow: hidden;
}
.detail_img{
	position: absolute;
	top: 50%;
	-webkit-transform:translateY(-50%);
	transform:translateY(-50%);
	width: 100%;
}
.detail_info:after{
	content: "";
	position: absolute;
	width: 100%;
	height: 4.26667rem;
	left: 0;
	bottom: 0;
	background: -webkit-gradient(linear,0% top,0% bottom, from(transparent), to(rgba(0,0,0,0.7)));
    background: -webkit-linear-gradient(top,rgba(0,0,0,0),rgba(0,0,0,0.7));
}
.detail_tag{
	position: absolute;
	top: 0;
	left: 10px;
	font-size: 12px;
}
.detail_tag a{
	display: inline-block;
	padding: 10px 0;
	height: 18px;
}
.detail_tag_txt{
	display: block;
	padding: 0 6px;
	height: 18px;
	line-height: 18px;
	border-radius: 18px;
	color: rgba(255,255,255,0.8);
	background: rgba(0,0,0,0.5);
}
.detail_star{
	position: absolute;
	bottom: 6px;
	left: 0;
	font-size: 12px;
	height: 20px;
	color: rgba(255,255,255,1);
	width: 100%;
  	display: -webkit-box;
  	display: -webkit-flex;
  	display: flex;
  	z-index: 1;
}
.detail_star span{
	padding: 0 10px;
  	-webkit-box-flex: 1;
  	-webkit-flex: 1;          
  	flex: 1;  
}
.detail_love{
	position: absolute;
	right: 0;
	top: 0;
	padding: 10px;
	width: 18px;
	height: 16px;
	background: url("data:image/png;base64,") no-repeat;
	background-size: 18px 16px;
	background-position: 50% 50%;
}
.detail_love.loved{
	background: url("data:image/png;base64,") no-repeat;
	background-size: 18px 16px;
	background-position: 50% 50%;
}
.detail_cnt{
	padding: 10px;
}
.detail_tit{
	position: relative;
	color: #171717;
	font-size: 18px;
	padding: 6px 0;
	overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
	text-align: center;
}
.detail_tit:after{
	content: "";
	position: absolute;
	width: 20px;
	height: 2px;
	background: #e4a558;
	left: 50%;
	bottom: 0;
	margin-left: -10px;
}
.detail_analysis{
	position: relative;
	color: #7d7d7d;
	font-size: 14px;
	line-height: 20px;
	padding: 10px 10px 0;
}
.detail_analysis p{
	margin-bottom: 12px;
}
.detail_analysis a{
	color: #6190e8;
}
.detail_analysis .ar_full_sub{
	display: block;
	color: #ccc;
	padding-bottom: 12px;
	text-align: center;
}
.detail_analysis .figurecapture{
	font-size: 12px;
	color: #ccc;
	text-align: center;
}
.detail_analysis .img img{
	width: 100%;
	height: 100%;
}
.detail_analysis .quote{
	position: relative;
	color: #bcbcbc;
	padding: 0 16px;
	font-style: italic;
/*	border-left: 3px #ccc solid;
	padding-left: 10px;*/
}
.detail_analysis .quote:before{
	content: "“";
	position: absolute;
	font-size: 30px;
	left: 0;
	top: 0;
}
.detail_analysis .author{
	color: #ccc;
	text-align: right;
}
.detail_analysis h3{
	padding-top: 20px;
	font-size: 16px;
	color: #171717;
}
.detail_bottom_btn{
	position: fixed;
	width: 36px;
	height: 36px;
	bottom: 10px;
	border-radius: 50%;
	background: rgba(97, 144, 232, 0.8);
	z-index: 9;
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
}
.detail_bottom_btn{
	left: 10px;
}
.detail_bottom_btn:after{
	content: "";
	position: absolute;
	width: 8px;
	height: 8px;
	left: 14px;
	top: 50%;
	margin-top: -6px;
	border: 2px solid #fff;
	border-color: #fff transparent transparent #fff;
	-webkit-transform: rotate(-45deg);
	transform: rotate(-45deg);
}
.detail_bottom_right{
	position: fixed;
	width: 100px;
	height: 36px;
	bottom: 10px;
	right: 10px;
	line-height: 36px;
	text-align: center;
	border-radius: 36px;
	background: rgba(97, 144, 232, 0.8);
	z-index: 9;
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
}
.detail_bottom_right:after{
	content: "";
	width: 1px;
	height: 12px;
	position: absolute;
	background: #fff;
	top: 12px;
	left: 44px;
	opacity: 0.8;
}
.detail_bottom_love{
	position: absolute;
	height: 36px;
	width: 30px;
	padding: 0 10px;
	left: 0;
	top: 0;
	background: url("data:image/png;base64,") no-repeat;
	background-size: 18px 16px;
	background-position: 50% 50%;
}
.detail_bottom_love.loved{
	background: url("data:image/png;base64,") no-repeat;
	background-size: 18px 16px;
	background-position: 50% 50%;	
}
.detail_bottom_curvol{
	position: absolute;
	color: #fff;
	height: 36px;
	width: 35px;
	padding-right: 15px;
	right: 0;
	top: 0;
}
.detail_bottom.hide{
	-webkit-transform: translateY(50px);
	transform: translateY(50px);
}

Page._content:
@charset "UTF-8";
/*===================*/
html{height:100%;-webkit-user-select:none;}
body, h1, h2, h3, h4, h5, h6, hr, p, dl, dt, dd, ul, ol, li, pre, form,button, input, textarea, th, td { margin: 0; padding: 0; vertical-align: baseline; }
img { border: 0 none; vertical-align: top; width: 100%;}
i, em { font-style: normal; }
ol, ul { list-style: none; }
input, select, button, h1, h2, h3, h4, h5, h6 { font-size: 100%; font-family: inherit; }
table { border-collapse: collapse; border-spacing: 0; }
a { text-decoration: none;-webkit-tap-highlight-color:rgba(0,0,0,0);}
a:hover { text-decoration: none;}
body { margin: 0 auto; min-width: 320px; max-width: 640px; height: 100%; background: #FFF; font-size: 0.6rem; font-family: Helvetica, 'STHeiti STXihei', 'Microsoft JhengHei', 'Microsoft YaHei', Arial;}
.clear { *zoom:1 }
.clear:before, .clear:after { display:table; content:"" }
.clear:after { clear:both }
.hide{display:none;}

/*=========sprite==========*/
.co_logo,
.fc_logo,
.ch_pic,
.ch_pic .ch_item_icon,
.ch_text,
.ch_text .ch_item_icon,
.ar_shake,
/*shake修改 by Yetty 12.23*/
.ar_shake:after{background: url("../img/sprite.png") no-repeat;background-size: 9.51467rem 7.59467rem;}

.co_logo,.fc_logo{background-position: 0 0}
.ch_pic{background-position: 0 -3.11467rem}
.ch_pic .ch_item_icon{background-position: -6.89067rem 0}
.ch_text{background-position: -5.03467rem -3.11467rem}
.ch_text .ch_item_icon{background-position: -6.912rem -1.55733rem}
.ar_shake{background-position: -3.28533rem 0}
/*shake修改 by Yetty 12.23*/
.ar_shake:after{background-position: -3.28533rem -1.70667rem;}
#index{z-index:23;}

/*===================*/
.wrap{position:absolute;width:100%;height:100%;min-width:320px;top:0;left:0;/*-webkit-transform:translateY(100%);transform:translateY(100%);*//*overflow:hidden;*/}
    .wrap .page{position:relative;width:100%;height:100%;background-color: #fff;/*overflow:hidden;*/}
/*=========cover==========*/
.wrap .fcover{border: solid 0.362667rem #6190e8;box-sizing:border-box;z-index:10;background-color:#A2C0F9;-webkit-transition:opacity ease .2s;transition:opacity ease .2s;}
	.fc_logo{top:6.37867rem;}
	.fc_tit{top:9.536rem;}
	.fc_btn{position:absolute;width: 1.87733rem;height: 1.87733rem;border-radius: 50%;border: 1px solid #fff;bottom:7.808rem;left: 50%;-webkit-transform:translateX(-50%);}
	.fc_btn:after{content: "";position: absolute; width: 0.426667rem;height: 0.426667rem;border-top: 1px solid #fff;border-right: 1px solid #fff; top: 50%;left: 50%; margin-left: -0.149333rem;-webkit-transform:translate(-50%,-50%) rotate(45deg);}

/*add by EC*/
.wrap .page{width:100%;height:100%;position:absolute;top:0;left:0;}
	.cover{z-index:100;-webkit-transition:all ease 1s;transition:all ease 1s;}
	.loaded{opacity:0;}
	.choose{z-index:99;-webkit-transition:all ease .4s;transition:all ease .4s;}

/*=========cover==========*/
.cover{border: solid 0.362667rem;box-sizing:border-box;}
	.co_info{position: relative;display: inline-block;height: 2.304rem;padding: 0 0.384rem;text-align: center;color: #fff;line-height: 0.853333rem;left: 50%;-webkit-transform:translateX(-50%);}
		.co_date{font-size: 0.469333rem;padding-top: 0.170667rem;}
		.co_vol{font-size: 0.896rem;}
	.co_img{position: absolute;width: 12.3733rem;height: 12.3733rem;top: 16.584%;left: 50%;-webkit-transform:translateX(-50%);border-radius: 50%;overflow: hidden;background-repeat:no-repeat;background-position:center;background-size:100% auto;}
	.co_logo,.fc_logo{position: absolute;width: 3.2rem;height: 3.2rem;left: 50%;-webkit-transform:translateX(-50%);}
	.co_tit,.fc_tit{position: absolute;width: 100%;font-size: 0.896rem;color: #fff;font-weight: normal;text-align: center;}
	.co_logo{background-image:url(../img/thumb.png);background-size:100% auto;bottom: 4.18133rem;}
    .co_tit{bottom: 2.60267rem;height: 1.19467rem;}

/*=========choose==========*/
.choose{border: solid 0.362667rem;box-sizing:border-box;}
	.ch_ask{position: absolute; font-size: 1.92rem;line-height: 1.92rem;width: 1.92rem;top: 3.05333rem;left: 50%;-webkit-transform:translateX(-50%);color: #fff;text-align: center;}
	.ch_item{position: absolute;display: block;width: 4.56533rem;height: 3.072rem;padding-top: 4.77867rem; bottom: 6.82133rem;font-size: 1.45067rem;line-height: 1.536rem;color: #fff;}
		.ch_item_icon{display: inline-block;height: 1.38667rem;margin-left:0.213333rem;}
	.ch_pic{left: 1.83467rem;}
		.ch_pic .ch_item_icon{width:1.25867rem;}
	.ch_text{right: 1.83467rem;}
		.ch_text .ch_item_icon{width:1.36533rem;}
	.ch_aside{width:100%;position:absolute;left:50%;bottom:1.1rem;font-size:1.024rem;text-align:center;color:#FFFFFF;-webkit-transform:translateX(-50%);transform:translateX(-50%);}
		.ch_aside span{display:block;}
		/*.ch_aside i{border:1px solid #FFFFFF;padding:0 2px;margin-right:2px;}*/
		.aside_txt1{font-size:0.6826rem;}
		.aside_txt2{font-size:0.5973rem;}

/*=========article==========*/
.article{position:relative;overflow:hidden;}
	.ar_tool{height: 2.55rem;text-indent: -9999px;position: relative;margin: 0 0.85rem;z-index:10;}
		.ar_list{position: absolute;display: block;width: 0.832rem;height: 0.832rem;background:url("data:image/png;base64,") no-repeat;background-size: 0.810667rem 0.810667rem; top:0.85rem;}
		.ar_sort{position: absolute;display: block;width: 0.725333rem;height: 0.704rem;background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACEAAAAgBAMAAABulgz5AAAAD1BMVEUAAAD///////////////+PQt5oAAAABHRSTlMA5kpJG2qUMwAAAEBJREFUKM9jMHQBAWEGBmYREMPRgAFIgxgMDIwuYCDA4MIABCCSxQFIA8khIILpC0UwLcTAwATxqQIDlcBIDkMAClEvBXMkhT4AAAAASUVORK5CYII=") no-repeat;background-size: 0.704rem 0.682667rem; top:0.95rem;left: 1.65rem;}
		.ar_search{position: absolute;width: 9.2rem;height: 1.25rem;font-size: 0.6rem;top:0.65rem;right: 0;}
			.ar_search_input{position: absolute; border: none; padding: 0 1.5rem 0 0.45rem; width: 7.25rem;height: 1.25rem;line-height: 1.25rem;color: #fff;background-color:rgba(255,255,255,0.4);border-radius: 0.625rem;right: 0;}
			.ar_search_btn{position: absolute;display: block;width: 0.725333rem;height: 0.768rem;background: url("data:image/png;base64,") no-repeat;background-size: 0.725333rem 0.768rem;right: 0.5rem;top: 0.25rem;}
.ar_wrap{width:100%;height:100%;position:absolute;top:0;left:0;background-color:#6190e8;background-repeat:no-repeat;background-size:100% 100%;-webkit-transition:all ease .8s;transition:all ease .8s;}
	.ar_sec{width:100%;position:relative;background-position:center;background-size:auto 100%;}
	.ar_sec:after{width:100%;height:100%;position:absolute;top:0;left:0;background:rgba(0,0,0,0.5);content:"";}
	.ar_sec_wrap{position:absolute;width:14.3147rem; overflow: hidden;top:0;bottom: 0; background-color: #fff;margin: 2.55rem 0;border-radius: 0.2rem;z-index:5;left:50%;-webkit-transform: translateX(-50%);-webkit-transition:all ease .8s;transition:all ease .8s;}

.prev .ar_sec_wrap{-webkit-transform: translate(-50%,4rem);opacity: 0.3;}
/*.prev .ar_sec_wrap:after{content:"";width:100%;height:100%;position:absolute;top:0;left:0;background:rgba(0,0,0,0.5);z-index:10;-webkit-transition:all ease 1s;transition:all ease 1s;}*/
.next .ar_sec_wrap{-webkit-transform: translate(-50%,-4rem);opacity: 0.8;}

		.ar_img{width:100%;padding-top:47.462%;position:relative;display:block;background:#E8E8E8 url(../img/thumb.png) no-repeat center;background-size:20% auto;overflow:hidden;}
			.ar_img img{width:100%;position:absolute;top:50%;left:0;-webkit-transform:translateY(-50%);transform:translateY(-50%);}
		.ar_cnt{padding:0 0.45rem;}
			.ar_tit{position: relative;font-size: 0.768rem;font-weight: normal;margin-top: 0.554667rem;height: 0.768rem;line-height: 0.768rem;padding-right: 3.2rem;overflow:hidden;white-space:nowrap;text-overflow:ellipsis;}
				.ar_tit a{color: #333;}
				.ar_type{display:block;box-sizing:border-box; position: absolute;right: 0;top: 0;text-align: center;height: 0.768rem;line-height: 0.768rem; width: 2.98667rem; font-size: 0.469333rem;border: 1px solid #333;border-radius: 0.15rem 0.15rem 0.15rem 0;}
				.ar_type_0{border-color: #000000;color: #000000;}
				.ar_type_1{border-color: #E12228;color: #E12228;}
				.ar_type_2{border-color: #0088CC;color: #0088CC;}
				.ar_type_3{border-color: #FFA900;color: #FFA900;}
				.ar_type_4{border-color: #4CBB1B;color: #4CBB1B;}
			.ar_attr{font-size: 0.597333rem;color: #999;margin-bottom: 0.213333rem;overflow: hidden;}
			.ar_tag{margin:0.554667rem 0 0.32rem 0;height: 0.896rem; overflow: hidden;}
				.ar_tag span{display: block;float: left; font-size: 0.512rem;color: #666;background-color: #eaeaea;padding: 0 0.341333rem;border-radius: 0.426667rem;margin-right: 0.213333rem;height: 0.896rem;line-height: 0.896rem;}
			.ar_stars{width: 50%;float: left;}
			.ar_star{position:relative;display: inline-block;}
				.ar_star:after{position:absolute;top:0;left:0;content:"★★★★★";color:#DADADA}
				.ar_star i{color:#FDC574;z-index:1;position:relative;display: inline-block;}
			.ar_analysis{font-size: 0.6rem;color: #666;line-height: 0.9rem;margin-bottom: 1rem;}
				.ar_analysis a{color:#389EFF;}
			.ar_more{position: absolute; text-align: center;color:#888;width: 100%; height: 2.13333rem; line-height: 2.13333rem;left: 0;bottom: 0;padding-top: 4.26667rem;background:-webkit-gradient(linear,0% top,0% bottom, from(transparent), to(#ffffff));background:-webkit-linear-gradient(top,rgba(255,255,255,0),rgba(255,255,255,1))}
			/*.ar_more span{display: inline-block;width: 0.3rem;height: 0.3rem;border-radius: 50%;background-color: #ddd;margin-top:0.5rem;}*/
			.ar_sec_wrap .ar_love{position: absolute;top: 0;right: 0;padding:10px;}
			.ar_full .ar_love{display:block;text-align:center;padding:30px 0 50px;}
				.ar_love_wrap{min-width:26px; height: 26px;background-color: rgba(0,0,0,0.38); border: 3px solid rgba(255,255,255,0.38);border-radius: 26px;line-height: 26px;text-align: center;position:relative;}
				.ar_full .ar_love_wrap{display:inline-block;}
				.ar_full .ar_love.loved .ar_love_wrap{background-color:rgba(255,255,255,1);border-color:rgba(249,79,79,0.38);}
				.ar_love_icon{position:absolute; display:block;width: 18px;height: 14px;background: url("data:image/png;base64,") no-repeat;background-size: 18px 14px;top:6px;left: 10px;}
				.ar_love_num{color:#FFFFFF;padding:0 8px 0 30px;}
				.ar_full .ar_love_txt{display:block;color:#999;margin-top:5px;}
				.ar_full .ar_love.loved .ar_love_txt{color:#F94F4F;}
			.ar_love.loved .ar_love_icon{background: url("data:image/png;base64,") no-repeat;background-size: 18px 14px;}
			.ar_love.loved .ar_love_num{color: #F94F4F;}
	.ar_mask{position: absolute;top: 0;left: 0;width: 100%;height: 100%;z-index: 11;overflow:hidden;background-color: rgba(0,0,0,0.6);-webkit-transition:all ease .4s;transition:all ease .4s;}
		.mask_hide{opacity:0;}
		.mask_show{opacity:1;-webkit-animation:show ease 1s;animation:show ease 1s;}
@-webkit-keyframes show{
	0%{opacity:0;}
	30%, 100%{opacity:1;}
}

		.ar_mask .mask{width:14.2933rem; height:20rem; background-color: #fff;margin-top: 2.55rem;border-radius: 0.2rem;position:absolute;top:0;left:50%;margin-left:-7.14665rem;}
		.mask_show .mask{-webkit-animation:maskScale ease 1s;animation:maskScale ease 1s;}
@-webkit-keyframes maskScale{
	0%{opacity:0;-webkit-transform:scaleX(1) scaleY(1);}
	30%{opacity:1;-webkit-transform:scaleX(1) scaleY(1);}
	60%, 100%{opacity:1;-webkit-transform:scaleX(1.2) scaleY(1.6);}
}

		.ar_full_close{width:100%;height:1.7rem;font:0/0 a;position:absolute;top:0;left:0;z-index:3;background:#FFFFFF;border-bottom: 1px solid #ddd;opacity:0.9;}
			.ar_full_close:before, .ar_full_close:after{width:1.28rem;height:1px;background:#BDBDBD;position:absolute;content:"";top:50%;right:0.3rem;}
			.ar_full_close:before{-webkit-transform:rotate(45deg);transform:rotate(45deg);}
			.ar_full_close:after{-webkit-transform:rotate(-45deg);transform:rotate(-45deg);}
		.ar_full{height:100%;position: relative;overflow-x:hidden;overflow-y:scroll;background-color: #fff;padding:0 0.64rem 0.64rem;z-index:2;-webkit-overflow-scrolling:touch;}
			.mask_show .ar_full_close, .mask_show .ar_full{-webkit-animation:textShow ease 1s;animation:textShow ease 1s;}
@-webkit-keyframes textShow{
	0%, 60%{opacity:0;}
	100%{opacity:1;}
}

		    .ar_full_tit{font-size: 1.06667rem; margin-top:1.7rem;line-height: 1; padding:0.35rem 0;font-weight: normal;}
		    .ar_full_tit a{color: #333;}
		    .ar_full_sub{font-size: 0.768rem;color: #999;line-height:0.682667rem;font-weight: normal;}
		    .ar_full p{font-size: 0.768rem;line-height:1.2rem;color: #666;margin-bottom: 0.7rem;}
		    .ar_full strong{color:#000000;}
		    .ar_full a{color:#389EFF;word-wrap:break-word;}
		    .ar_full .img{background:#EEEEEE;}
		    .ar_full img{width:100%;display:block;margin:0 auto;background:#EEEEEE;}
		    .ar_full video{width:100%;display:block;background:#EEEEEE;}

	.ar_nav{display:none;position: absolute;bottom:1.05rem;left: 50%;-webkit-transform:translateX(-50%);}

	/*shake修改 by Yetty 12.23*/
