	/*图片模式 by Yetty 12.21*/
	.pic_mode .ar_analysis,
	.pic_mode .ar_more,
	.pic_mode .ar_attr li:nth-child(1),
	.pic_mode .ar_attr li:nth-child(2),
	.pic_mode .ar_attr li:nth-child(4){display: none;-webkit-transition:all ease .5s;transition:all ease .5s;}
	.pic_mode .ar_img{height: 17.7067rem;padding-top: 0;-webkit-transition:all ease .5s;transition:all ease .5s;}

	.text_mode .ar_analysis,
	.text_mode .ar_more,
	.text_mode .ar_attr li,
	.text_mode .ar_img{-webkit-transition:all ease .5s;transition:all ease .5s;}


