.loading{width:100%;height:100%;position:fixed;top:0;left:0;background:#F5F5F5;z-index:100;opacity:1;-webkit-transition:all ease .2s .2s;}
	.loaded{opacity:0;}
	.loading div{position:absolute;top:50%;left:50%;-webkit-animation:rotate ease 1s infinite;}
		.loading div i{width:0;height:12px;position:absolute;border:2px dashed transparent;border-left:2px solid #E12228;}
			.loading div i:nth-child(1){top:-10px;left:-2px;-webkit-transform:translate(0px, 0px) rotate(30deg);-webkit-animation:certrifuge1 ease 1s infinite;}
			.loading div i:nth-child(2){border-left-color:#0088CC;top:-10px;right:-7px;-webkit-transform:translate(0px, 0px) rotate(150deg);-webkit-animation:certrifuge2 ease 1s infinite;}
			.loading div i:nth-child(3){border-left-color:#FFA900;bottom:-11px;-webkit-transform:translate(0px, 0px) rotate(-90deg);-webkit-animation:certrifuge3 ease 1s infinite;}

		@-webkit-keyframes rotate{
			0%{-webkit-transform:rotate(0deg);}
			100%{-webkit-transform:rotate(360deg);}
		}
		@-webkit-keyframes certrifuge1{
			0%, 100{-webkit-transform:translate(0px, 0px) rotate(30deg);}
			50%{-webkit-transform:translate(10px, -6px) rotate(30deg);}
		}
		@-webkit-keyframes certrifuge2{
			0%, 100{-webkit-transform:translate(0px, 0px) rotate(150deg);}
			50%{-webkit-transform:translate(10px, 6px) rotate(150deg);}
		}
		@-webkit-keyframes certrifuge3{
			0%, 100{-webkit-transform:translate(0px, 0px) rotate(-90deg);}
			50%{-webkit-transform:translate(-10px, 6px) rotate(-90deg);}
		}

