.btn{
	position: fixed;
	width: 36px;
	height: 36px;
	bottom: 10px;
	left: 10px;
	border-radius: 50%;
	background: rgba(97, 144, 232, 0.8);
	z-index: 9;
}
.btn span{
	position: absolute;
	width: 16px;
	height: 2px;
	background: #fff;
	left: 50%;
	margin-left: -8px;
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
}
.btn span:nth-child(1){
	top: 11px;
}
.btn span:nth-child(2){
	top: 17px;
}
.btn span:nth-child(3){
	top: 23px;
}
.top{
	position: fixed;
	width: 36px;
	height: 36px;
	bottom: 10px;
	right: 10px;
	border-radius: 50%;
	background: rgba(97, 144, 232, 0.8);
	z-index: 9;
}
.top:after{
	content: "";
	position: absolute;
	width: 8px;
	height: 8px;
    top: 14px;
    left: 50%;
    margin-left: -6px;
	border: 2px solid #fff;
	border-color: #fff transparent transparent #fff;
	-webkit-transform: rotate(45deg);
	transform: rotate(45deg);
}
.menu{
	position: fixed;
	width: 240px;
	height: 100%;
	-webkit-transform: translatex(-240px);
	transform: translatex(-240px);
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
	top: 0;
	left: 0;
}
.menu_main{
	position: absolute;
	width: 240px;
	height: 100%;
	background: #6190e8;
	top: 0;
	left: 0;
	overflow: auto;
	-webkit-overflow-scrolling: touch;
	z-index: 3;
	box-shadow: -5px 0 5px rgba(0,0,0,0.2) inset;
}
.menu_logo{
	width: 60px;
	height: 60px;
	margin: 30px auto 0;
	background: url("http://jdc.jd.com/h5/case/img/thumb.png");
	background-size: 100%;
}
.menu_search{
	position: relative;
    width: 210px;
    height: 30px;
    margin: 20px auto 0;
}
.menu_search_input{
    border: none;
    padding: 0 35px;
    width: 140px;
    height: 30px;
    text-align: center;
    line-height: 30px;
    color: #fff;
    background-color: rgba(255,255,255,0.4);
    border-radius: 30px;
}
.menu_search_input::-webkit-input-placeholder{
	color: rgba(255,255,255,0.5);
}
.menu_search_btn{
	position: absolute;
	right: 10px;
	top: 6px;
	width: 17px;
	height: 18px;
	background: url("data:image/png;base64,");
	background-size: 100%;
}
.menu_list{
	position: relative;
	margin: 30px 15px 50px;
	text-align: center;
}
.menu_list:before{
	content: "";
	position: absolute;
	width: 20px;
	height: 2px;
	background: #e4a558;
	left: 50%;
	margin-left: -10px;
	top: -12px;
}
.menu_list:after{
	clear: both;
	content: "";
	display: block;
	position: relative;
	top: -1px;
	height: 1px;
	background: #6190e8;
}
.menu_item{
	position: relative;
	color: #fff;
	font-size: 12px;
	box-sizing: border-box;
	line-height: 50px;
	height: 50px;
	width: 70px;
	padding: 0 5px;
	float: left;
	border: 1px solid rgba(255,255,255,0.2);
	border-width: 0 1px 1px 0;
	opacity: 0.85;
	overflow: hidden;     
	white-space:nowrap;
    text-overflow:ellipsis;
}
.menu_item:nth-child(3n){
	border-width: 0 0 1px 0;
}
.menu_item.cur{
	background: rgba(0,0,0,0.1);
	opacity: 1;
}
.menu_item.cur:after{
	width: 0;
}
.menu_item:last-child:after{
	width: 0;
}
.menu_mask{
	position: absolute;
	width: 100%;
	height: 100%;
	left: 240px;
	top: 0;
}
.menu_show{
	overflow: hidden;
}
.menu_show .case{
	-webkit-transform: translatex(240px) scale(1.12);
	transform: translatex(240px) scale(1.12);
	-webkit-transform-origin: 0% 50%;
	transform-origin: 0% 50%;
}
.menu_show .menu{
	-webkit-transform: translatex(0);
	transform: translatex(0);
}
.menu_show .btn{
	background: rgba(97, 144, 232, 0);
}
.menu_show .btn span:nth-child(1){
	-webkit-transform: rotate(45deg);
	transform: rotate(45deg);
	-webkit-transform-origin: 0% 50%;
	transform-origin: 0% 50%;
}
.menu_show .btn span:nth-child(2){
	opacity: 0;
}
.menu_show .btn span:nth-child(3){
	-webkit-transform: rotate(-45deg);
	transform: rotate(-45deg);
	-webkit-transform-origin: 0% 50%;
	transform-origin: 0% 50%;
}
::-webkit-scrollbar {display:none} 
.loading{
	position: absolute;
	height: 100%;
	width: 100%;
	background: #6190e8;
	z-index: 10;
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
}
.load_main{
	position: absolute;
	top: 50%;
	left: 50%;
	-webkit-transform: translate(-50%,-50%);
	transform: translate(-50%,-50%);
}
.load .main{
	stroke-dasharray: 855;
	stroke-dashoffset: 855;
	fill: rgba(0,0,0,0);
	-webkit-animation: main 2s linear infinite;
}
@-webkit-keyframes main{
	0%{ stroke-dashoffset: 855;fill: rgba(0,0,0,0);}
	20%{ stroke-dashoffset: 0;fill: rgba(0,0,0,0);}
	40%,100%{ stroke-dashoffset: 0;fill: #fff;}
}
.load .eye_l,
.load .eye_r{
	-webkit-transform: scale(1,0);
	transform-origin: 36.25% 60%;
	-webkit-animation: eye 2s infinite;
}
@-webkit-keyframes eye{
	0%,40%,61%{ -webkit-transform: scale(1,0);}
	60%,100%{ -webkit-transform: scale(1,1);}
}
.load .line_t,
.load .line_b{
	opacity: 0;
	-webkit-transform: translateY(20px);
}
.load .line_t{
	-webkit-animation: line_t 2s infinite;
}
.load .line_b{
	-webkit-animation: line_b 2s infinite;
}
@-webkit-keyframes line_t{
	0%,60%{ opacity: 0;-webkit-transform: translateY(20px);}
	80%,100% { opacity: 1;-webkit-transform: translateY(0);}
}
@-webkit-keyframes line_b{
	0%,40%{ opacity: 0;-webkit-transform: translateY(20px);}
	60%,100% { opacity: 1;-webkit-transform: translateY(0);}
}

