	position: relative;
	color: #7d7d7d;
	font-size: 12px;
	line-height: 18px;
	height: 36px;
	padding: 10px 10px 0;
	overflow: hidden;
	text-overflow: ellipsis;
	display: -webkit-box;
	-webkit-line-clamp: 2;
	-webkit-box-orient: vertical;
}

.case_item_analysis:after{
	content: "";
	position: absolute;
	width: 100%;
	height: 36px;
	left: 0;
	bottom: 0;
	background: -webkit-gradient(linear,0% top,0% bottom, from(transparent), to(rgba(255,255,255,0.7)));
    background: -webkit-linear-gradient(top,rgba(255,255,255,0),rgba(255,255,255,0.7));
}
.case_item_more{
	display: block;
	margin: 10px auto;
	font-size: 12px;
	border-radius: 30px;
	width: 100px;
	height: 28px;
	line-height: 28px;
	color: #fff;
	background: #6190e8;
}
.case_collection .case_item_analysis,
.case_collection .case_item_mark,
.case_collection .case_item_more{
	display: none;
}
