.btn{
	position: fixed;
	width: 36px;
	height: 36px;
	bottom: 10px;
	left: 10px;
	border-radius: 50%;
	background: rgba(97, 144, 232, 0.8);
	z-index: 9;
}
.btn span{
	position: absolute;
	width: 16px;
	height: 2px;
	background: #fff;
	left: 50%;
	margin-left: -8px;
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
}
.btn span:nth-child(1){
	top: 11px;
}
.btn span:nth-child(2){
	top: 17px;
}
.btn span:nth-child(3){
	top: 23px;
}
.top{
	position: fixed;
	width: 36px;
	height: 36px;
	bottom: 10px;
	right: 10px;
	border-radius: 50%;
	background: rgba(97, 144, 232, 0.8);
	z-index: 9;
}
.top:after{
	content: "";
	position: absolute;
	width: 8px;
	height: 8px;
    top: 14px;
    left: 50%;
    margin-left: -6px;
	border: 2px solid #fff;
	border-color: #fff transparent transparent #fff;
	-webkit-transform: rotate(45deg);
	transform: rotate(45deg);
}
.menu{
	position: fixed;
	width: 240px;
	height: 100%;
	-webkit-transform: translatex(-240px);
	transform: translatex(-240px);
	-webkit-transition: all 0.5s ease;
	transition: all 0.5s ease;
	top: 0;
	left: 0;
}
.menu_main{
	position: absolute;
	width: 240px;
	height: 100%;
	background: #6190e8;
	top: 0;
	left: 0;
	overflow: auto;
	-webkit-overflow-scrolling: touch;
	z-index: 3;
	box-shadow: -5px 0 5px rgba(0,0,0,0.2) inset;
}
/*.menu_main:after{
	content: "";
	position: absolute;
	width: 10px;
	height: 100%;
	right: 0;
	top: 0;
	background: -webkit-gradient(linear,0% left,0% right, from(rgba(0,0,0,0)), to(rgba(0,0,0,0.2)));
    background: -webkit-linear-gradient(left,rgba(0,0,0,0),rgba(0,0,0,0.2));
}*/
.menu_logo{
	width: 60px;
	height: 60px;
	margin: 30px auto 0;
	background: url("http://jdc.jd.com/h5/case/img/thumb.png");
	background-size: 100%;
}
.menu_search{
	position: relative;
    width: 210px;
    height: 30px;
    margin: 20px auto 0;
}
.menu_search_input{
    border: none;
    padding: 0 35px;
    width: 140px;
    height: 30px;
    text-align: center;
    line-height: 30px;
    color: #fff;
    background-color: rgba(255,255,255,0.4);
    border-radius: 30px;
}
.menu_search_input::-webkit-input-placeholder{
	color: rgba(255,255,255,0.5);
}
.menu_search_btn{
	position: absolute;
	right: 10px;
	top: 6px;
	width: 17px;
	height: 18px;
	background: url("data:image/png;base64,");
	background-size: 100%;
}
.menu_list{
	position: relative;
	margin: 20px auto 60px;
	text-align: center;
}
.menu_list:after{
	content: "";
	position: absolute;
	width: 20px;
	height: 2px;
	background: #e4a558;
	left: 50%;
	margin-left: -10px;
	top: -2px;
}
.menu_item{
	position: relative;
	color: #fff;
	font-size: 14px;
	line-height: 50px;
	height: 50px;
	padding: 0 15px;
	opacity: 0.85;
	overflow: hidden;     
	white-space:nowrap;
    text-overflow:ellipsis;
	/*border-bottom: 1px solid rgba(255,255,255,0.3);
	border-width: 0.5px;*/
}
.menu_item:after{
	content: "";
	position: absolute;
	width: 120px;
	height: 1px;
	background: rgba(255,255,255,0.3);
	left: 50%;
	bottom: 0;
	margin-left: -60px;
	-webkit-transform: scale(0.5);
	transform: scale(0.5);
}
.menu_item.cur{
	background: rgba(0,0,0,0.1);
	border-left: 3px solid #fff;
	opacity: 1;
}
.menu_item.cur:after{
	width: 0;
}
.menu_item:last-child:after{
	width: 0;
}
.menu_mask{
	position: absolute;
	width: 100%;
	height: 100%;
	left: 240px;
	top: 0;
}
.menu_show{
	overflow: hidden;
}
.menu_show .case{
	-webkit-transform: translatex(240px) scale(1.12);
	transform: translatex(240px) scale(1.12);
	-webkit-transform-origin: 0% 50%;
	transform-origin: 0% 50%;
}
.menu_show .menu{
	-webkit-transform: translatex(0);
	transform: translatex(0);
}
.menu_show .btn{
	background: rgba(97, 144, 232, 0);
}
.menu_show .btn span:nth-child(1){
	-webkit-transform: rotate(45deg);
	transform: rotate(45deg);
	-webkit-transform-origin: 0% 50%;
	transform-origin: 0% 50%;
}
.menu_show .btn span:nth-child(2){
	opacity: 0;
}
.menu_show .btn span:nth-child(3){
	-webkit-transform: rotate(-45deg);
	transform: rotate(-45deg);
	-webkit-transform-origin: 0% 50%;
	transform-origin: 0% 50%;
}
::-webkit-scrollbar {display:none}

